import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'User Register';
  message = '';
  registered = false;

  UserRegister(){
    this.registered = true;
    this.message = 'the user was successfully registered';

  }
}
