import { Component, Input } from '@angular/core';
import { Employee } from '../employee.componet';

@Component({
  selector: 'app-employee-son-c',
  templateUrl: './employee-son-c.component.html',
  styleUrls: ['./employee-son-c.component.css']
})
export class EmployeeSonCComponent {

  @Input() employee:Employee;
  @Input() i:number;


  arrayCarateristics = [''];
  addCarateristic(newCarateristic:string){
    this.arrayCarateristics.push(newCarateristic);
  }
}
<div class="container">
    <div class="row">
      <div class="col">
        <div>
          {{i+1}} {{employee.name}} {{employee.surname}} {{employee.charge}} {{employee.salary}}
          <a [routerLink]="['/update-employee', i]" [queryParams]="{action:'1'}">
            Update
          </a>
          &nbsp;
          <a [routerLink]="['/update-employee', i]" [queryParams]="{action:'2'}">
            Delete
          </a>
        </div>

        <app-carateristics-employee-comp (employeeCarateristics)="addCarateristic($event)"></app-carateristics-employee-comp>
        <ul style="list-style:none">
          <li *ngFor="let carateristic of arrayCarateristics">{{carateristic}}</li>
        </ul>
      </div>
    </div>
</div>
