import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { EmployeeServiceService } from '../employee-service.service';
import { DataServiceService } from '../data-service.service';
import { Employee } from '../employee.componet';

@Component({
  selector: 'app-update-employee',
  templateUrl: './update-employee.component.html',
  styleUrls: ['./update-employee.component.css']
})
export class UpdateEmployeeComponent implements OnInit {

  constructor(private router:Router, private myService:EmployeeServiceService, 
    private employeeService:DataServiceService, private route:ActivatedRoute){}


    ngOnInit(): void {
      this.indice=this.route.snapshot.params['id'];

      let employeeUP:Employee=this.employeeService.findEmployee(this.indice);

      this.nameSpace = employeeUP.name;
      this.surnameSpace = employeeUP.surname;
      this.chargeSpace = employeeUP.charge;
      this.salarySpace = employeeUP.salary;  
    }

  AddEmployee(){
    let myEmployee = new Employee(this.nameSpace, this.surnameSpace, this.chargeSpace, this.salarySpace);
    //this.myService.ShowMessage("EmployeeName: " + myEmployee.name + " " + myEmployee.surname)
    //this.employees.push(myEmployee);
    //this.employees = [ ...this.employees, myEmployee ];
    this.employeeService.addEmployeeService(myEmployee);

    this.goHome();

  };

  nameSpace:string = "";
  surnameSpace:string = "";
  chargeSpace:string = "";
  salarySpace:number = 0;

  indice:number=0;

goHome(){
  this.router.navigate([""]);
}
}
<div class="container">
    <div class="col">
        <h1 style="text-align: center;">Update Employee</h1>

        <Form class="form-group row">
            <div class="form-group col-md-3">
                <label for="name">Name:</label><br>
                <input type="text" name="name" id="name" placeholder="Name" class="form-control" [(ngModel)]="nameSpace">
            </div>

            <div class="form-group col-md-3">
                <label for="surname">Surname:</label><br>
                <input type="text" name="surname" id="surname" placeholder="Surname" class="form-control" [(ngModel)]="surnameSpace">
            </div>

            <div class="form-group col-md-3">
                <label for="charge">Charge:</label><br>
                <input type="text" name="charge" id="charge" placeholder="Charge" class="form-control" [(ngModel)]="chargeSpace">
            </div>

            <div class="form-group col-md-3">
                <label>Salary:</label><br>
                <input type="number" name="salary" id="salary" placeholder="Salary" class="form-control" [(ngModel)]="salarySpace">
            </div>

            <div class="col">
                <button type="submit" class="btn btn-primary" (click)="AddEmployee()" style="margin-top: 20px;">Update</button>
            </div>

        </Form>
    </div>
</div>
