//Vamos ha utilizar firebase -> realtime database

import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Employee } from "./employee.componet";

@Injectable()
export class DataServices{

    constructor(private httpClient:HttpClient) {}

    GetEmployees(){
       return this.httpClient.get('https://my-clients-67552-default-rtdb.firebaseio.com/datos.json');
        
    }

    saveEmployees(employees:Employee[]){
        this.httpClient.put('https://my-clients-67552-default-rtdb.firebaseio.com/datos.json', employees).subscribe(
            response=> console.log("The information was save "+ response),

            error => console.log("Error: " + error),
        );

    };

    
}
