import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { LoginService } from './login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
//https://www.youtube.com/watch?v=wGv3mjvLtdY&list=PLU8oAlHdN5BnNAe8zXnuBNzKID39DUwcO&index=40
export class LoginComponent {

  constructor(private loginService:LoginService){}

  login(form:NgForm){
    const email = form.value.email;
    const password = form.value.password;

    this.loginService.login(email, password);

  }
}
<div style="display: flex;justify-content: center;">

    <form class="form-group row" style="margin-top: 20px;" #myForm = "ngForm" (ngSubmit)="login(myForm)">
        <div class="form-group col-md-6">
            <input type="email" name="email" placeholder="Email" class="form-control" ngModel>
        </div>
        <div class="form-group col-md-6">
            <input type="password" name="password" placeholder="Password" class="form-control" ngModel>
        </div>
        <br/><br/>
        <div style="margin-bottom: 10px; justify-content: center; display: flex;">
            <button type="submit" class="btn btn-primary">Log In</button>
        </div>
    </form>
</div>
