import { Component } from '@angular/core';

@Component({
  selector: 'app-employee',
  templateUrl: './employee.component.html',   
  //template:"<p>Here goes an employee</p>",   Html inline
  styleUrls: ['./employee.component.css']    
  //styles:['p{background-color:blue}']        Css inline
})
export class EmployeeComponent {

  name="Juan";
  surname="Acevedo";
  age=26;
  //company="Team International"

  //getAge(){
  //  return this.age;
  //}

  //saveCompany(value:String){          event click

  //}

  ableBox = true;
  checkboxValue= false;

  setUserRegister(){
    alert("The user is already logged")
  }

}
<p>Employee name: {{name}}</p>
<p>Employee surname: {{surname}}</p>
<p>Employee age: {{age < 18? 'Menor de edad': 'Mayor de edad'}}</p>
<p>Employee company: {{company.value}}</p>

Introduce your company: <input type="text" #company (keyup)="0" [disabled]="ableBox">
<br/>
<br/>
<!--<input type="button" value="save!" (click)="saveCompany(company.value)"/>-->

Is Logged : <input type="checkbox" id="logging" [checked]="checkboxValue">
<br/>
